const MovieCard = ({ movie }) => {
// Check if movie exists and has posterUrl, title, and genre properties
   if (!movie || !movie.posterUrl || !movie.title || !movie.genre) {
    return null; // Return nothing if any required property is missing
  }
  
  return (
    <div className="movie-card">
    <div className="max-w-sm bg-white rounded-lg shadow-md overflow-hidden m-4">
    <img className="w-full h-64 object-cover" src={movie.posterUrl} alt={movie.title} />
      <div className="p-4">
      <h3 className="text-lg font-semibold">{movie.title}</h3>
      <p className="text-sm text-gray-500">{movie.genre}</p>
    </div>
    </div>
   </div>
  );
};
export default MovieCard;
